import Paper from "@mui/material/Paper";
import Typography from "@mui/material/Typography";

import { useEffect } from "react";

import { useState } from "react";
import { Box } from "@mui/system";
import Button from "@mui/material/Button";
import { collection, query, orderBy, onSnapshot,doc, updateDoc, deleteDoc } from "firebase/firestore";
import { db } from "../../firebase.js";


function Quests({ name, completed, userId }) {
  const [checked, setChecked] = useState(completed);
  const [quest, setQuest] = useState([]);
  // const f = async () => {
  //   try {
  //     const questRequest = await axios({
  //       method: "get",
  //       url: "http://localhost:3001/quests",
  //       headers: {
  //         Authorization: "BEARER " + token,
  //       },
  //     });
  //     setQuest(questRequest.data);
  //   } catch (e) {
  //     console.log(e);
  //   }
  // };
  // f();
  const handleChange = async () => {
    const taskDocRef = doc(db, "adventurerDB/quests", userId);
    try {
      await updateDoc(taskDocRef, {
        completed: checked,
      });
    } catch (err) {
      alert(err);
    }
  };
  const handleDelete = async () => {
    const taskDocRef = doc(db, "adventurerDB/quests", userId);
    try {
      await deleteDoc(taskDocRef);
    } catch (err) {
      alert(err);
    }
  };

  const HandleSubmit = () => {
    // questId = id;
    //const data = new FormData(event.currentTarget);
    // eslint-disable-next-line no-console
    useEffect(() => {
      const taskColRef = query(
        collection(db, "adventurerDB/quests"),
        orderBy("created", "desc")
      );
      onSnapshot(taskColRef, (snapshot) => {
        setQuest(
          snapshot.docs.map((doc) => ({
            id: doc.id,
            data: doc.data(),
          }))
        );
      });
    }, []);
    // useEffect(() => {
    //   const completeRequest = axios({
    //     method: "post",
    //     url: `http://localhost:3001/complete/:${questId}`,
    //     headers: {
    //       Authorization: "BEARER " + token,
    //     },
    //   });
    //   console.log(completeRequest.status);
    // }, []);
  };

  const myQuest = quest.map((a) => (
    <Paper
      elevation={10}
      sx={{
        padding: "15px",
        heigth: "400px",
      }}
    >
      {" "}
      <Typography variant="h6">{a.name}</Typography>
      <Typography>level {a.level}</Typography>
      <Typography>Complete for {a.completionXp} xp</Typography>
      <Button onSubmit={() => handleChange(a._id)} noValidate>
        COMPLETE
      </Button>
    </Paper>
  ));
  return (
    <Paper
      elevation={10}
      sx={{
        padding: "15px",
        heigth: "400px",
      }}
    >
      <Box>
        <Typography variant="h5">To complete</Typography>
        {myQuest}
        <Typography variant="h5">Complete</Typography>
      </Box>
    </Paper>
  );
}

export default Quests;
