import Paper from "@mui/material/Paper";
import Typography from "@mui/material/Typography";
import * as React from "react";
import { useEffect } from "react";
import Checkbox from "@mui/material/Checkbox";
import { useState } from "react";
import { Box } from "@mui/system";
import Button from "@mui/material/Button";
import {
  collection,
  query,
  onSnapshot,
  doc,
  updateDoc,
  deleteDoc,
  where,
} from "firebase/firestore";
import { db } from "../../firebase.js";

function Quests(props) {
  const { userId } = props;
  const [checked, setChecked] = useState(false);

  const [quest, setQuest] = useState([]);

  const handleChange = async (e, id) => {
    const taskDocRef = doc(db, "adventurerDB", userId, "quests", id);
    try {
      await updateDoc(taskDocRef, {
        completed: checked,
      });
    } catch (err) {
      alert(err);
    }
    setChecked(e.target.checked);
  };
  const handleDelete = async (id) => {
    const taskDocRef = doc(db, "adventurerDB", userId, "quests", id);
    try {
      await deleteDoc(taskDocRef);
    } catch (err) {
      alert(err);
    }
  };

  useEffect(() => {
    const taskColRef = query(
      collection(db, "adventurerDB", userId, "quests"),
      where("userId", "==", userId)
    );
    console.log(taskColRef);
    onSnapshot(taskColRef, (snapshot) => {
      setQuest(
        snapshot.docs.map((doc) => ({
          id: doc.id,
          data: doc.data(),
        }))
      );
    });
  }, []);
  console.log(quest);
  const myQuest = quest.map((a) => (
    // <Paper
    //   elevation={10}
    //   sx={{
    //     padding: "15px",
    //     heigth: "400px",
    //   }}
    // >
    //   {" "}
    //   <Typography variant="h6">{a.name}</Typography>
    //   <Typography>level {a.userId}</Typography>
    //   {/* <Button onSubmit={() => (onChange = { handleChange })} noValidate>
    //     COMPLETE
    //   </Button> */}
    //   <div>
    //     <input
    //       id={`checkbox-${userId}`}
    //       className="checkbox-custom"
    //       name="checkbox"
    //       checked={checked}
    //       onChange={handleChange}
    //       type="checkbox"
    //     />
    //     <label
    //       htmlFor={`checkbox-${userId}`}
    //       className="checkbox-custom-label"
    //       onClick={() => setChecked(!checked)}
    //     ></label>
    //     </div>
    // </Paper>
    <div>
      {/* <div>
        <input
          id={`checkbox-${a.data.userId}`}
          className="checkbox-custom"
          name="checkbox"
          checked={checked}
          onchange={handleChange(a.id)}
          type="checkbox"
        />
        <label
          htmlFor={`checkbox-${a.data.name}`}
          className="checkbox-custom-label"
          onClick={() => setChecked(!checked)}
        >
          {a.data.name}
        </label>
      </div> */}
      <Checkbox
        checked={checked}
        label={a.data.name}
        onChange={handleChange}
        inputProps={{ "aria-label": "controlled" }}
      >
      </Checkbox>
      {a.data.name}
      {/* <div>
        <div>
          <div>
            <button onclick={handleDelete(a.id)}>Delete</button>
          </div>
        </div>
      </div> */}
      <div>
        {" "}
        <Button onClick={handleDelete} variant="contained">
          Delete
        </Button>
      </div>
    </div>
  ));

  return (
    <Paper
      elevation={10}
      sx={{
        padding: "15px",
        heigth: "400px",
      }}
    >
      <Box>{myQuest}</Box>
    </Paper>
  );
}

export default Quests;
